import React, { useState, useEffect, useRef } from 'react';
import './MultiChatXpertPage.css';
import userImage from './images/Chips.png'
import aiImage from './images/BreachHours.jpg'
import axios from 'axios';

function MultiChatXpertPage() {
  const [messages, setMessages] = useState([]);
  const [isTTSOpen, setTTSOpen] = useState(false);
  const [isSpeechRecognitionOpen, setSpeechRecognitionOpen] = useState(false);
  const [isLLMSelectionOpen, setLLMSelectionOpen] = useState(false);
  const [isUpdateExpertsOpen, setUpdateExpertsOpen] = useState(false);
  const [isExpertPopupOpen, setExpertPopupOpen] = useState(false);
  const [isConversationHistoryOpen, setConversationHistoryOpen] = useState(false);
  const [experts, setExperts] = useState([
    "Computer Engineer", "Mechanical Engineer", "Ethical AI Expert"
  ]);
  const [selectedExperts, setSelectedExpert] = useState(null);
  const [languageModels, setLanguageModels] = useState([
    "ChatGPT", "Google Gemini", "Anthropic Claude"
  ]);
  const [currentLLM, setCurrentLLM] = useState(null);

  const openPopup = (setting) => {
    setTTSOpen(false);
    setSpeechRecognitionOpen(false);
    setLLMSelectionOpen(false);
    setUpdateExpertsOpen(false);
    setConversationHistoryOpen(false);

    if (setting === "TTS") setTTSOpen(true);
    if (setting === "SpeechRecognition") setSpeechRecognitionOpen(true);
    if (setting === "LLMSelection") setLLMSelectionOpen(true);
    if (setting === "UpdateExperts") setUpdateExpertsOpen(true);
    if (setting === "Conversation History") setConversationHistoryOpen(true);
  };

  const selectExpert = (expert) => {
    setSelectedExpert(expert);
    setExpertPopupOpen(false); // Close popup once expert is selected
  };

  const selectLLM = (languageModels) => {
    setCurrentLLM(languageModels)
  }

  function getCsrfToken() {
    const name = 'csrftoken';
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + '=')) {
        return cookie.substring(name.length + 1);
      }
    }
    return null;
  }
  
  const handleSendMessage = async (message) => {
    console.log("Sending message:", message); // Log the message before sending
    try {
      const response = await axios.post(
        'https://bada-2601-702-0-9480-e902-94e6-a4ed-8872.ngrok-free.app/api/analyze_text/', 
        {
          text: message,
          selected_experts: JSON.stringify(selectedExperts),  // Experts data
          selected_option: JSON.stringify(currentLLM)          // LLM option
        },
        {
          headers: {
            'X-CSRFToken': getCsrfToken(),  // Pass CSRF token in header if available
          },
          withCredentials: true  // Allows cross-origin credentials (cookies, CSRF token)
        }
      );
  
      const aiText = response.data.output || "No response from AI";
      
      const aiResponse = { text: aiText, isUser: false };
setMessages((prevMessages) => [...prevMessages, aiResponse]);
    } catch (error) {
      console.error('Error sending message to backend:', error);
    }
  };

  return (
    <div className="my-ui-container">
      {/* AI Image Container */}
      <div className="ai-container">
        <button className="ai-image" onClick={() => setExpertPopupOpen(true)}>
          <img src={aiImage} alt="AI Expert" />
        </button>
      </div>

      {/* Console Container */}
      <div className="console-container">
        <div className="settings-content">
          {currentView === "default" ? (
            <div className="console-option">
              {options[currentOptionIndex]}
            </div>
          ) : (
            <div className="settings-options">
              <h3>{options[currentOptionIndex]}</h3>
              {/* Place your content here for each settings page */}
              <button onClick={handleBack} className="back-button">Back</button>
            </div>
          )}
        </div>
      </div>

      {/* User Image Container */}
      <div className="user-container">
        <UserIconPopup />
      </div>

      {/* Conversation Window Container */}
      <div className="conversation-container">
        <ConversationWindow messages={messages} />
      </div>

      {/* Entry Box Container */}
      <div className="entry-box-container">
        <InputSection
          onPromptSubmit={(prompt) => {
            const newMessage = { text: prompt, isUser: true };
            setMessages([...messages, newMessage]);
            handleSendMessage(prompt);  // Send the message to the backend
          }}
          onFileUpload={(file) => console.log('File uploaded:', file.name)}
          onCopyConversation={() => navigator.clipboard.writeText(messages.map(m => m.text).join('\n'))}
        />
      </div>
    </div>
  );
}

// Define Console Component with Option Cycling
function Console({ openPopup }) {
  const [currentView, setCurrentView] = useState("default"); // default, TTS, SpeechRecognition, LLMSelection, UpdateExperts, ConversationSelect
  const options = ["Text-To-Speech", "Speech Recognition", "LLM Selection", "Update Experts", "Conversation Select"];
  const [currentOptionIndex, setCurrentOptionIndex] = useState(0);

  const handleSelectOption = () => {
    const selectedOption = options[currentOptionIndex];
    setCurrentView(selectedOption.replace(/ /g, '')); // Switch view to selected option
  };

  const handleBack = () => setCurrentView("default"); // Go back to the main settings menu

  const handleScroll = (direction) => {
    if (direction === 'up') {
      setCurrentOptionIndex((prev) => (prev === 0 ? options.length - 1 : prev - 1));
    } else {
      setCurrentOptionIndex((prev) => (prev === options.length - 1 ? 0 : prev + 1));
    }
  };

  return (
    <div className="console-grid">
      <div className="settings-display">
        {currentView === "default" ? (
          <div className="console-option">
            {options[currentOptionIndex]}
          </div>
        ) : (
          <SettingsPage currentView={currentView} handleBack={handleBack} />
        )}
      </div>
      {currentView === "default" && (
        <div className="settings-buttons">
          <button onClick={() => handleScroll('up')} className="up-arrow">▲</button>
          <button onClick={handleSelectOption} className="select-button">Select</button>
          <button onClick={() => handleScroll('down')} className="down-arrow">▼</button>
        </div>
      )}
      <VolumeSlider />
    </div>
  );
}

function SettingsPage({ currentView, handleBack }) {
  const [conversations, setConversations] = useState([]);

  useEffect(() => {
    // Clear the conversations each time the page is loaded or refreshed
    setConversations([]); 
  }, []);

  return (
    <div className="settings-container">
      <div className="settings-content">
        {currentView === "TextToSpeech" && (
          <>
            <h3>Text-To-Speech</h3>
            <button onClick={() => console.log('TTS Enabled')}>Enable</button>
            <button onClick={() => console.log('TTS Disabled')}>Disable</button>
          </>
        )}
        {currentView === "SpeechRecognition" && (
          <>
            <h3>Speech Recognition</h3>
            <button onClick={() => console.log('Start Recording')}>Record Speech</button>
            <button onClick={() => console.log('Check Recording')}>Check Recording</button>
          </>
        )}
        {currentView === "LLMSelection" && (
          <>
            <h3>LLM Selection</h3>
            <select onChange={(e) => console.log(`Selected LLM: ${e.target.value}`)}>
              <option>ChatGPT</option>
              <option>Google Gemini</option>
              <option>Anthropic Claude</option>
            </select>
          </>
        )}
        {currentView === "UpdateExperts" && (
          <>
            <h3>Update Experts</h3>
            <div className="expert-grid">
              {[...Array(15)].map((_, i) => (
                <button key={i} onClick={() => console.log(`Edit Expert ${i + 1}`)}>
                  Expert {i + 1}
                </button>
              ))}
            </div>
          </>
        )}
        {currentView === "ConversationSelect" && (
          <>
            <h3>Conversation History</h3>
            <div className="conversation-history">
              {conversations.length > 0 ? (
                conversations.map((conversation, i) => (
                  <button key={i} onClick={() => console.log(`Selected Conversation ${i + 1}`)}>
                    Conversation {i + 1} - Timestamp
                  </button>
                ))
              ) : (
                <p>No conversations available.</p>
              )}
            </div>
          </>
        )}
      </div>
      <button onClick={handleBack} className="back-button">Back</button>
    </div>
  );
}

function VolumeSlider() {
  const [volume, setVolume] = useState(50);

  return (
    <div className="volume-slider-container">
      <span className="volume-label">TTS Volume:</span>
      <input
        type="range"
        className="volume-slider"
        min="0"
        max="100"
        value={volume}
        onChange={(e) => setVolume(Number(e.target.value))}
      />
      <span className="volume-label"> {volume}</span>
    </div>
  );
}

// Define ExpertPopup Component
function ExpertPopup({ experts, onSelectExpert }) {
  return (
    <div className="popup expert-popup">
      <h3>Select AI Expert</h3>
      {experts.map((expert, index) => (
        <button key={index} onClick={() => onSelectExpert(expert)} className="expert-option">
          {expert}
        </button>
      ))}
      <button onClick={() => onSelectExpert(null)}>Cancel</button>
    </div>
  );
}

// Define UserIconPopup Component
function UserIconPopup() {
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      console.log('User icon changed to:', file.name);
    }
  };

  return (
    <div className="user-icon-popup">
      <button className="user-image">
        <img src={userImage} alt="User Icon" />
      </button>
      <input type="file" id="icon-upload" accept="image/*" onChange={handleFileChange} style={{ display: 'none' }} />
    </div>
  );
}

// Define ConversationWindow Component with Auto-Scrolling
function ConversationWindow({ messages }) {
  const conversationRef = useRef(null);

  // Auto-scroll to the bottom of the conversation when new messages are added
  useEffect(() => {
    if (conversationRef.current) {
      conversationRef.current.scrollTop = conversationRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="conversation-window" ref={conversationRef}>
      {messages.map((message, index) => (
        <div key={index} className={`message-bubble ${message.isUser ? 'user-message' : 'ai-message'}`}>
          <p>{message.text}</p>
        </div>
      ))}
    </div>
  );
}

// Define InputSection Component with Expanding Entry Box
function InputSection({ onPromptSubmit, onFileUpload, onCopyConversation }) {
  const [prompt, setPrompt] = useState("");

  const handleInputChange = (e) => {
    setPrompt(e.target.value);
    
    // Adjust height only if content exceeds the current visible area
    e.target.style.height = "auto";  // Reset height to auto to calculate new height
    if (e.target.scrollHeight > e.target.clientHeight) {
      e.target.style.height = `${e.target.scrollHeight}px`;  // Expand based on content
    }
  };

  const handleFileUpload = (e) => {
    if (e.target.files.length > 0) {
      onFileUpload(e.target.files[0]);
    }
  };

  const handleBlur = (e) => {
    if (prompt === "") {
      e.target.style.height = "20px";  // Only shrink to 20px if there's no content
    }
  };

  return (
    <div className="input-section">
      <textarea
        value={prompt}
        onChange={handleInputChange}
        onBlur={handleBlur}
        className="prompt-input"
        placeholder="Type your message here..."
        rows={1}
        style={{ height: "20px" }}  // Set default height to 20px
      />
      <button onClick={() => onPromptSubmit(prompt)}>Submit</button>
      <button onClick={onCopyConversation}>Copy Conversation</button>
      <button onClick={() => document.getElementById('file-upload').click()}>Upload File</button>
      <input type="file" id="file-upload" style={{ display: 'none' }} onChange={handleFileUpload} />
    </div>
  );
}

// Additional Popup Components (Text-to-Speech, Speech Recognition, LLM Selection, Update Experts)
function TextToSpeechPopup() {
  return (
    <div className="popup">
      <h3>Text-To-Speech</h3>
      <button onClick={() => console.log('TTS Enabled')}>Enable</button>
      <button onClick={() => console.log('TTS Disabled')}>Disable</button>
    </div>
  );
}

function SpeechRecognitionPopup() {
  return (
    <div className="popup">
      <h3>Speech Recognition</h3>
      <button onClick={() => console.log('Start Recording')}>Record Speech</button>
      <button onClick={() => console.log('Check Recording')}>Check Recording</button>
      <button onClick={() => console.log('Exit Recording')}>Exit</button>
    </div>
  );
}

function LLMSelectionPopup() {
  return (
    <div className="popup">
      <h3>LLM Selection</h3>
      <button onClick={() => console.log('ChatGPT Selected')}>ChatGPT</button>
      <button onClick={() => console.log('Google Gemini Selected')}>Google Gemini</button>
      <button onClick={() => console.log('Anthropic Claude Selected')}>Anthropic Claude</button>
    </div>
  );
}

function UpdateExpertsPopup() {
  return (
    <div className="popup">
      <h3>Update Experts</h3>
      {/* Render buttons and expert updating UI here */}
    </div>
  );
}

function ConversationHistoryPopup() {
  return (
    <div className="popup">
      <h3>Conversation History</h3>
      {/* Render Conversation History and Selection In Console Here */}
    </div>
  );
}

export default MultiChatXpertPage;
